        private static string CreateUsersAndRoles(SearchServiceDBContext context, UserManager<AppUser> userManager, RoleManager<IdentityRole> roleManager)
        {
            var EnsolAdmin = "EndocsAdmin";
            var ClientAdmin = "ClientAdmin";
            var User = "Users";
            var adminRoleId = string.Empty;
            var userId = string.Empty;

            var extUser = "ExternalSystem";
            var extUseremail = "ext@endocs.cloud";
            var EnsolAdminEmail = "support@endocs.cloud";
            var EnsolAdminUserName = "EnDocs";

            if (!roleManager.RoleExistsAsync(ClientAdmin).Result)
            {
                var role = new IdentityRole(ClientAdmin);
                var res = roleManager.CreateAsync(role).Result;
            }
            if (!roleManager.RoleExistsAsync(User).Result)
            {
                var role = new IdentityRole(User);
                var res = roleManager.CreateAsync(role).Result;
            }
            var HasEnsolAdmin = !roleManager.RoleExistsAsync(EnsolAdmin).Result;
            if (HasEnsolAdmin)
            {
                var role = new IdentityRole(EnsolAdmin);
                adminRoleId = role.Id;
                roleManager.CreateAsync(role).Wait();
            }
            else
            {
                var role = roleManager.FindByNameAsync(EnsolAdmin);
                role.Wait();
                var res = role.Result;
                adminRoleId = res.Id;
            }

            if (userManager.FindByEmailAsync(EnsolAdminEmail).Result == null)
            {
                var user = new AppUser { UserName = EnsolAdminEmail, Email = EnsolAdminEmail, EmailConfirmed = true, DisplayName = EnsolAdminUserName };
                var res = userManager.CreateAsync(user, "P@ssw0rd").Result;
                if (res.Succeeded)
                {
                    userManager.AddToRoleAsync(user, ClientAdmin).Wait();
                    userManager.AddToRoleAsync(user, EnsolAdmin).Wait();
                }
            }

            if (userManager.FindByNameAsync(extUseremail).Result == null)
            {
                var user = new AppUser { UserName = extUser, Email = extUseremail, EmailConfirmed = true, DisplayName = extUser };
                userManager.CreateAsync(user, ",/?qZ;+L2E^=Eg96").Wait();
            }
            var UserOut = userManager.FindByEmailAsync(EnsolAdminEmail).Result;
            return UserOut.Id;
        }

